import React, { useState, useEffect } from "react";
function Temp() {
  const [city, setCity] = useState("");
  const [buttonCity, setButtonCity] = useState();
  const [weatherData, setWeatherData] = useState("Mumbai");
  useEffect(() => {
    const fetchApi = async () => {
      const response = await fetch(
        `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=41562a6fa663497de0a473ee54ed9d87&units=metric`
      );
      const data = await response.json();
      setWeatherData(data)
    };
    fetchApi();
  },[weatherData]);
  function handleChange(event) {
    var val = event.target.value;
    setCity(val);
  }
  function buttonClick() {
    return setButtonCity(city);
  }
  return (
    <div className="box">
      <div className="heading">
        <h1>Weather App</h1>
        <div className="image">
          <img
            src="https://cdn.icon-icons.com/icons2/2211/PNG/512/weather_sun_sunny_cloud_icon_134157.png"
            height={35}
            width={33}
          />
        </div>
      </div>

      <div className="inputField">
        <input type="search" name="inputBox" onChange={handleChange} />
        <button type="submit" onClick={buttonClick}>
          <i class="fa-solid fa-magnifying-glass"></i>
        </button>
      </div>
      {city?<div className="text">
        <h1>{buttonCity}</h1>
        <h2>{city.main.temp}Â°C</h2>
        <p>{city.weather[0].description}</p>
      </div>:<p>No data found with that city name..</p>}
      
    </div>
  );
}
export default Temp;
